import React, {useState, useEffect} from 'react';
import CardListItem from './CardListItem';


function GameCard({omenCardShuffle, onButtonClick}) {

    if(omenCardShuffle.length == 0){
        return "loading";
    }


    const omenCardItems = omenCardShuffle[0]

    // if (omenCardShuffle.might == undefined)
    //     return 

    const handleClick = function() {
        onButtonClick(omenCardShuffle)
    }

    return (

        <>
        <div className="text-box">
            <p className="text"> {omenCardItems.name} onButtonClick={onButtonClick}</p>
            <p className="text"> {omenCardItems.flavourText} </p>
            <p className="text"> Might: {omenCardItems.might} </p>
        </div>
        </>
    )


}

export default GameCard;
